CREATE OR REPLACE FUNCTION obter_cid_princ_atend_ipasgo ( cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nr_atendimento_p atendimento_paciente.nr_atendimento%type) RETURNS DIAGNOSTICO_DOENCA.CD_DOENCA%TYPE AS $body$
DECLARE


dt_diagnostico_w		diagnostico_medico.dt_diagnostico%type;
cd_doenca_w			diagnostico_doenca.cd_doenca%type;
ie_forma_cid_princ_ipasgo_w	parametro_faturamento.ie_forma_cid_princ_ipasgo%type := 'U';

C01 CURSOR FOR
	SELECT	cd_doenca
	from	diagnostico_doenca
	where	nr_atendimento	= nr_atendimento_p
	and	dt_diagnostico	= dt_diagnostico_w
	and	ie_classificacao_doenca = 'P'
	order by dt_atualizacao;


BEGIN

begin
select	coalesce(ie_forma_cid_princ_ipasgo,'U')
into STRICT	ie_forma_cid_princ_ipasgo_w
from	parametro_faturamento
where	cd_estabelecimento = cd_estabelecimento_p  LIMIT 1;
exception
when others then
	ie_forma_cid_princ_ipasgo_w := 'U';
end;

if (ie_forma_cid_princ_ipasgo_w = 'U') then
	begin

	begin
	select	max(dt_diagnostico)
	into STRICT	dt_diagnostico_w
	from	diagnostico_medico
	where	nr_atendimento	= nr_atendimento_p
	and	ie_tipo_diagnostico = 2;
	exception
	when no_data_found then
		dt_diagnostico_w := null;
	end;

	end;
elsif (ie_forma_cid_princ_ipasgo_w = 'P') then
	begin

	begin
	select	min(dt_diagnostico)
	into STRICT	dt_diagnostico_w
	from	diagnostico_medico
	where	nr_atendimento	= nr_atendimento_p
	and	ie_tipo_diagnostico = 2;
	exception
	when no_data_found then
		dt_diagnostico_w := null;
	end;

	end;
end if;

open c01;
	loop
	fetch c01 into
		cd_doenca_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	end loop;
close c01;

return cd_doenca_w;

end;
$body$
