CREATE OR REPLACE FUNCTION obter_cid_proc_ama ( nr_prescricao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*IE_OPCAO_P:
C - Código
D - Descição
*/
ds_retorno_w		varchar(255);
cd_doenca_cid_w		varchar(10);
nr_seq_ama_w		bigint;


BEGIN

select	coalesce(nr_seq_ama,0)
into STRICT	nr_seq_ama_w
from	proced_medico_ambulatorial
where	nr_prescricao = nr_prescricao_p;

select	coalesce(cd_doenca_cid,'')
into STRICT	cd_doenca_cid_w
from	atend_medico_ambulatorial
where	nr_sequencia = nr_seq_ama_w;

if (ie_opcao_p = 'C') then
	begin
	ds_retorno_w := cd_doenca_cid_w;
	end;
elsif (ie_opcao_p = 'D') and (coalesce(cd_doenca_cid_w,'X') <> 'X') then
	begin
	ds_retorno_w := substr(obter_descricao_cid(cd_doenca_cid_w),1,255);
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
