CREATE OR REPLACE FUNCTION obter_cid_secun_atendimento ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


dt_diagnostico_w	timestamp;
cd_doenca_w		varchar(10);


C01 CURSOR FOR
	SELECT	cd_doenca
	from	diagnostico_doenca
	where	nr_atendimento	= nr_atendimento_p
	and	dt_diagnostico	= dt_diagnostico_w
	and	ie_classificacao_doenca = 'S'
	order by dt_atualizacao;


BEGIN

begin
select	max(dt_diagnostico)
into STRICT	dt_diagnostico_w
from	diagnostico_medico
where	nr_atendimento	= nr_atendimento_p
and	ie_tipo_diagnostico IN (2, 3);
exception
	when no_data_found then
		dt_diagnostico_w := null;
end;

OPEN C01;
	LOOP
	FETCH C01 into
		cd_doenca_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	
	END LOOP;
CLOSE C01;	

RETURN cd_doenca_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
