CREATE OR REPLACE FUNCTION obter_cid_sus_aten (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


cd_cid_principal_w	varchar(10);
dt_emissao_w	timestamp;
ds_cid_pricipal_w	varchar(50);



BEGIN
ds_cid_pricipal_w := '';

select	max(dt_emissao)
into STRICT	dt_emissao_w
from	sus_aih_unif
where	nr_atendimento = nr_atendimento_p;

if (dt_emissao_w IS NOT NULL AND dt_emissao_w::text <> '') then

	select	max(coalesce(cd_cid_principal,null))
	into STRICT	cd_cid_principal_w
	from	sus_aih_unif
	where	nr_atendimento = nr_atendimento_p
	and	dt_emissao = dt_emissao_w;

	ds_cid_pricipal_w := substr(sus_obter_desc_cid(cd_cid_principal_w),1,50);

end if;

return	ds_cid_pricipal_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
