CREATE OR REPLACE FUNCTION obter_cirurgias_atend_concat ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno	varchar(4000):= '';
ds_retorno2	varchar(80) := '';

C01 CURSOR FOR 
	SELECT	nr_cirurgia 
	from	cirurgia 
	where	nr_atendimento = nr_atendimento_p 
	and	Cih_Obter_Regra_Acomp_Pos_Alta(nr_atendimento_p,cd_procedimento_princ,ie_origem_proced,wheb_usuario_pck.get_cd_estabelecimento) = 'S';


BEGIN 
 
open C01;
loop 
fetch C01 into	 
	ds_retorno2;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
		ds_retorno := ds_retorno|| ds_retorno2||',';
	end;
end loop;
close C01;
 
if (ds_retorno IS NOT NULL AND ds_retorno::text <> '') then 
	ds_retorno := substr(ds_retorno,1,length(ds_retorno)-1);
end if;
 
return	ds_retorno;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
