SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_cirurgia_autor_convenio ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


nr_cirurgia_w			bigint;
nr_seq_agenda_w			autorizacao_convenio.nr_seq_agenda%type;
nr_atendimento_w		bigint;
cd_convenio_w			bigint;
ds_retorno_w			varchar(255);
ie_somente_cirur_w		varchar(15)	:= 'N';
qt_existe_proc_autor_w		bigint;
qt_existe_mat_autor_w		bigint;
dt_inicio_real_w		varchar(25);
/*
ie_opcao_p
NR - Numero da cirurgia
DT - Data da cirurgia
*/
BEGIN

ie_somente_cirur_w	:= coalesce(obter_valor_param_usuario(3004,109,obter_perfil_ativo,wheb_usuario_pck.get_nm_usuario,wheb_usuario_pck.get_cd_estabelecimento),'N');

select	nr_seq_agenda,
	nr_atendimento,
	cd_convenio
into STRICT	nr_seq_agenda_w,
	nr_atendimento_w,
	cd_convenio_w
from 	autorizacao_convenio
where	nr_sequencia	= nr_sequencia_p;

if (coalesce(nr_seq_agenda_w,0) > 0) then

	select	max(nr_cirurgia),
		max(TO_CHAR(dt_inicio_real,'dd/mm/yyyy hh24:mi:ss'))
	into STRICT	nr_cirurgia_w,
		dt_inicio_real_w
	from	cirurgia
	where	nr_seq_agenda		= nr_seq_agenda_w;

elsif (coalesce(nr_atendimento_w,0) > 0) then

	if (ie_somente_cirur_w = 'S') then
	
		select	count(*),
			max(a.nr_cirurgia),
			max(TO_CHAR(a.DT_INICIO_REAL,'dd/mm/yyyy hh24:mi:ss')) dt
		into STRICT	qt_existe_proc_autor_w,
			nr_cirurgia_w,
			dt_inicio_real_w
		from	cirurgia a,
			procedimento_paciente x,
			procedimento_autorizado	y
		where	x.nr_seq_proc_autor	= y.nr_sequencia
		and	x.nr_atendimento	= nr_atendimento_w
		and	a.nr_cirurgia		= x.nr_cirurgia
		and	y.nr_sequencia_autor	= nr_sequencia_p;
		
		if (qt_existe_proc_autor_w = 0) then
		
			select	count(*),
				max(c.nr_cirurgia),
				max(TO_CHAR(c.DT_INICIO_REAL,'dd/mm/yyyy hh24:mi:ss')) dt
			into STRICT	qt_existe_mat_autor_w,
				nr_cirurgia_w,
				dt_inicio_real_w
			from	material_atend_paciente a,
				material_autorizado b,
				cirurgia c
			where	a.nr_seq_mat_autor	= b.nr_sequencia
			and	a.nr_atendimento	= nr_atendimento_w
			and	a.nr_cirurgia		= c.nr_cirurgia
			and	b.nr_sequencia_autor	= nr_sequencia_p;
		end if;
		
		if (coalesce(qt_existe_mat_autor_w,0) = 0) and (coalesce(qt_existe_proc_autor_w,0) = 0) then
			
			nr_cirurgia_w 		:= null;
			dt_inicio_real_w	:= null;
					
		end if;
	elsif (ie_somente_cirur_w = 'N') then
	
		select	max(nr_cirurgia),
			max(TO_CHAR(DT_INICIO_REAL,'dd/mm/yyyy hh24:mi:ss')) dt
		into STRICT	nr_cirurgia_w,
			dt_inicio_real_w
		from	cirurgia
		where	nr_atendimento		= nr_atendimento_w
		and	cd_convenio		= cd_convenio_w;
		
	end if;
end if;

if (ie_opcao_p = 'NR') then
	ds_retorno_w	:= nr_cirurgia_w;
ELSIF (ie_opcao_p = 'DT') THEN
	ds_retorno_w := dt_inicio_real_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
