SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_cirurgia_paciente ( nr_atendimento_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
 
ds_procedimento_w	varchar(1000);
cd_pessoa_fisica_w	varchar(10);
ds_retorno_w		varchar(1000);
nr_sequencia_w		bigint;
nr_cirurgia_w		bigint;

/* 
 
ie_opcao_p 
 
AA - Cirurgia agendada ou cirurgia do atendimento 
P - Procedimento da cirurgia(agendada ou realizada) 
 
*/
 
 

BEGIN 
 
select	obter_pessoa_atendimento(nr_atendimento_p,'C') 
into STRICT	cd_pessoa_fisica_w
;
 
if (ie_opcao_p = 'AA') or (ie_opcao_p = 'P')then 
	begin 
	 
	SELECT	MAX(nr_sequencia) 
	into STRICT	nr_sequencia_w 
	FROM	agenda_paciente a 
	WHERE	cd_pessoa_fisica	= cd_pessoa_fisica_w	 
	AND	hr_inicio > clock_timestamp() 
	AND	(cd_procedimento IS NOT NULL AND cd_procedimento::text <> '') 
	AND	ie_status_agenda NOT IN ('E','C') 
	AND	EXISTS ( SELECT 1 
		  	 FROM	agenda x 
			 WHERE a.cd_agenda 		 = x.cd_agenda	 
			 AND	x.cd_tipo_agenda 	 = 1 ); 	
 
	 
	select	p.ds_procedimento || CASE WHEN ie_opcao_p='AA' THEN  ' (Cirurgia agenda para ' || to_char(hr_inicio, 'dd/mm/yyyy hh24:mi:ss') || ')'  ELSE '' END  
	into STRICT	ds_procedimento_w 
	from	agenda_paciente a, 
		procedimento p 
	where	a.cd_procedimento 	= p.cd_procedimento 
	and	a.ie_origem_proced 	= p.ie_origem_proced 
	and	nr_sequencia		= nr_sequencia_w;
	 
	exception 
	when others then 
		ds_procedimento_w	:= null;
	end;	
 
 
	if (coalesce(ds_procedimento_w::text, '') = '') then 
 
		SELECT	MAX(nr_cirurgia) 
		into STRICT	nr_cirurgia_w 
		FROM	cirurgia 
		WHERE	nr_atendimento		= nr_atendimento_p 
		AND	ie_status_cirurgia 	= 2;
		 
		if (nr_cirurgia_w IS NOT NULL AND nr_cirurgia_w::text <> '') then 
			SELECT	p.ds_procedimento || CASE WHEN ie_opcao_p='AA' THEN  ' (Cirurgia realizada)'  ELSE '' END  
			into STRICT	ds_procedimento_w 
			FROM	cirurgia a, 
				procedimento p 
			WHERE	p.cd_procedimento 	= a.cd_procedimento_princ 
			AND	p.ie_origem_proced 	= a.ie_origem_proced 	 
			AND	nr_cirurgia		= nr_cirurgia_w;
		end if;
 
	end if;
	ds_retorno_w	:= ds_procedimento_w;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_cirurgia_paciente ( nr_atendimento_p text, ie_opcao_p text) FROM PUBLIC;

