CREATE OR REPLACE FUNCTION obter_classe_titulo_receber (nr_interno_conta_p bigint, nr_seq_protocolo_p bigint, nr_titulo_p bigint default null) RETURNS bigint AS $body$
DECLARE


ie_tipo_protocolo_w		smallint	:= null;
nr_seq_classe_w			bigint	:= null;
ie_origem_titulo_w		titulo_receber.ie_origem_titulo%type;

c01 CURSOR FOR
	SELECT	a.nr_seq_classe
	from	classe_titulo_receb_regra a,
			classe_titulo_receber b
	where	a.nr_seq_classe = b.nr_sequencia
	and		coalesce(a.ie_origem_titulo,coalesce(ie_origem_titulo_w,'0'))	= coalesce(ie_origem_titulo_w,'0')
	and		coalesce(a.ie_tipo_protocolo,coalesce(ie_tipo_protocolo_w,0))	= coalesce(ie_tipo_protocolo_w,0)
	and		coalesce(b.ie_situacao,'A') = 'A';


BEGIN


if (nr_seq_protocolo_p IS NOT NULL AND nr_seq_protocolo_p::text <> '') then
	select	max(ie_tipo_protocolo)
	into STRICT	ie_tipo_protocolo_w
	from	protocolo_convenio
	where	nr_seq_protocolo	= nr_seq_protocolo_p;
else
	select	max(a.ie_tipo_protocolo)
	into STRICT	ie_tipo_protocolo_w
	from	protocolo_convenio a,
		conta_paciente b
	where	a.nr_seq_protocolo	= b.nr_seq_protocolo
	and	b.nr_interno_conta	= nr_interno_conta_p;
end if;

select	max(a.ie_origem_titulo)
into STRICT	ie_origem_titulo_w
from	titulo_receber a
where	a.nr_titulo = nr_titulo_p;

open c01;
loop
fetch	c01 into nr_seq_classe_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
end loop;
close c01;


RETURN nr_seq_classe_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
