CREATE OR REPLACE FUNCTION obter_classificacao_atend (NR_ATENDIMENTO_P bigint, IE_OPCAO_P text) RETURNS varchar AS $body$
DECLARE


nr_seq_classificacao_w	bigint;
ds_classificacao_w	varchar(60);
ds_retorno_w		varchar(60);

/*
C	- Sequencia
D	- Descrição
*/
BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	select	max(nr_seq_classificacao)
	into STRICT	nr_seq_classificacao_w
	from	atendimento_paciente
	where	nr_atendimento = nr_atendimento_p;

	if (coalesce(ie_opcao_p, 'C') = 'C') then
		ds_retorno_w	:= nr_seq_classificacao_w;
	elsif (coalesce(ie_opcao_p, 'C') = 'D') then
		select 	max(ds_classificacao)
		into STRICT	ds_classificacao_w
		from 	classificacao_atendimento
		where 	nr_sequencia = nr_seq_classificacao_w;

		ds_retorno_w	:= ds_classificacao_w;
	end if;
end if;

RETURN	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
