CREATE OR REPLACE FUNCTION obter_classificacao_pf ( cd_pessoa_fisica_p text) RETURNS bigint AS $body$
DECLARE


nr_seq_classif_w	bigint;

C01 CURSOR FOR
	SELECT	nr_seq_classif
	from	pessoa_classif
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	order by	dt_inicio_vigencia;


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	open C01;
	loop
	fetch C01 into
		nr_seq_classif_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		nr_seq_classif_w := nr_seq_classif_w;
		end;
	end loop;
	close C01;
end if;

return	nr_seq_classif_w;

end;
