CREATE OR REPLACE FUNCTION obter_classif_agenda_atend ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(200);
nr_sequencia_w	numeric(20);


BEGIN

begin
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	select	max(nr_sequencia)
	into STRICT	nr_sequencia_w
	from	agenda_consulta
	where	nr_atendimento	= nr_atendimento_p;

	if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
		select	substr(obter_classif_agenda_turno(ie_classif_agenda, null), 1, 200)
		into STRICT	ds_retorno_w
		from	agenda_consulta
		where	nr_sequencia	= nr_sequencia_w;
	end if;
end if;


exception
when others then
		null;
end;

return	ds_retorno_w;

end;
