CREATE OR REPLACE FUNCTION obter_classif_agenda_oftalmo (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE



ds_retorno_w	varchar(255);

BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	select	max(substr(obter_classif_agenda_consulta(ie_classif_agenda),1,255))
	into STRICT	ds_retorno_w
	from	agenda_consulta
	where   nr_seq_oftalmo = nr_sequencia_p;
	if (coalesce(ds_retorno_w::text, '') = '') then
		select	max(substr(obter_desc_classif_agenda_pac(NR_SEQ_CLASSIF_AGENDA),1,255))
		into STRICT	ds_retorno_w
		from	agenda_paciente
		where   nr_seq_oftalmo = nr_sequencia_p;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
