CREATE OR REPLACE FUNCTION obter_classif_agenda_regra (cd_agenda_p bigint, cd_pessoa_fisica_p text, ie_tipo_agenda_p text) RETURNS varchar AS $body$
DECLARE


IE_CLASSIF_AGENDA_w	varchar(15);
nr_seq_regra_w		bigint;
qt_existe_w		bigint;


BEGIN

select 	max(nr_sequencia)
into STRICT	nr_seq_regra_w
from	REGRA_ATUAL_CLASSIF
where	ie_tipo_agenda = ie_tipo_agenda_p
and	ie_tipo_regra  = 'P';

if (nr_seq_regra_w IS NOT NULL AND nr_seq_regra_w::text <> '') then

	select 	count(*)
	into STRICT	qt_existe_w
	from	agenda_consulta
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	cd_agenda	 = cd_agenda_p
	and	ie_status_agenda not in ('F','I','C','R');

	if (qt_existe_w = 0) then

		select	IE_CLASSIF_AGENDA
		into STRICT	IE_CLASSIF_AGENDA_w
		from	REGRA_ATUAL_CLASSIF
		where	nr_sequencia = nr_seq_regra_w;
	end if;
end if;
return	IE_CLASSIF_AGENDA_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
