CREATE OR REPLACE FUNCTION obter_classif_assunto_pf ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_assunto_w		bigint;
ie_classificacao_w		varchar(1);

BEGIN

select	max(nr_seq_assunto)
into STRICT	nr_seq_assunto_w
from	relac_pf_instituicao
where	nr_sequencia	= nr_sequencia_p;

if (coalesce(nr_seq_assunto_w::text, '') = '') then
	ie_classificacao_w	:= 'A';
else
	select	coalesce(max(ie_classificacao),'A')
	into STRICT	ie_classificacao_w
	from	tipo_assunto_relac_pf
	where	nr_sequencia	= nr_seq_assunto_w;

end if;

return	ie_classificacao_w;

end;
$body$
