CREATE OR REPLACE FUNCTION obter_classif_atuacao_medico (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

			 
ds_retorno_w			varchar(80);
nr_seq_classif_medico_w		bigint;


BEGIN 
 
ds_retorno_w:= '';
 
select 	coalesce(max(nr_seq_classif_medico),0) 
into STRICT	nr_seq_classif_medico_w 
from 	atendimento_paciente 
where 	nr_atendimento = nr_atendimento_p;
 
