CREATE OR REPLACE FUNCTION obter_classif_circ_abdominal ( ie_sexo_p text, qt_circunferencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_classificacao_w	varchar(255);

BEGIN

if (ie_sexo_p IS NOT NULL AND ie_sexo_p::text <> '') and (qt_circunferencia_p IS NOT NULL AND qt_circunferencia_p::text <> '') then

	if (ie_sexo_p = 'M') and (qt_circunferencia_p >= 94) and (qt_circunferencia_p < 102) then

		ds_classificacao_w := substr(obter_valor_dominio_idioma(8113, '1', wheb_usuario_pck.get_nr_seq_idioma), 1, 255);

	elsif (ie_sexo_p = 'M') and (qt_circunferencia_p >= 102) then

		ds_classificacao_w := substr(obter_valor_dominio_idioma(8113, '2', wheb_usuario_pck.get_nr_seq_idioma), 1, 255);

	elsif (ie_sexo_p = 'F') and (qt_circunferencia_p >= 80) and (qt_circunferencia_p < 88) then

		ds_classificacao_w := substr(obter_valor_dominio_idioma(8113, '1', wheb_usuario_pck.get_nr_seq_idioma), 1, 255);

	elsif (ie_sexo_p = 'F') and (qt_circunferencia_p >= 88) then

		ds_classificacao_w := substr(obter_valor_dominio_idioma(8113, '2', wheb_usuario_pck.get_nr_seq_idioma), 1, 255);
	end if;

end if;

return	ds_classificacao_w;

end;
