SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_classif_ctb ( cd_classificacao_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/* ie_opcao_p
NIVEL =	NÃ­vel
SUPERIOR = Superior*/
ds_resultado_w		varchar(40);
i			integer;
ie_nivel_w		integer;
qt_posicao_w		integer;


BEGIN
ds_resultado_w		:= null;
qt_posicao_w		:= 0;
if (upper(ie_opcao_p) =  'NIVEL') and (cd_classificacao_p IS NOT NULL AND cd_classificacao_p::text <> '')  then
	begin
	ie_nivel_w				:= 1;
	for	i in 1..length(cd_classificacao_p) loop
		if (substr(cd_classificacao_p,i, 1) = '.')  then
			ie_nivel_w		:= ie_nivel_w + 1;
		end if;
	end loop;
	ds_resultado_w			:= ie_nivel_w;
	end;
elsif (upper(ie_opcao_p) =  'SUPERIOR') and (cd_classificacao_p IS NOT NULL AND cd_classificacao_p::text <> '')  then
	begin
	for	i in 1..length(cd_classificacao_p) loop
		if (substr(cd_classificacao_p,i, 1) = '.')  then
			qt_posicao_w		:= i;
		end if;
	end loop;
	if (qt_posicao_w	> 0) then
		ds_resultado_w			:= substr(cd_classificacao_p,1,qt_posicao_w - 1);
	end if;
	end;
end if;
return ds_resultado_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
