CREATE OR REPLACE FUNCTION obter_classif_etaria ( cd_estabelecimento_p bigint, cd_pessoa_fisica_p text, ie_codigo_descr_p text) RETURNS varchar AS $body$
DECLARE


dt_nascimento_w		timestamp;
dt_obito_w		timestamp;
qt_existe_w		bigint;
ds_retorno_w		varchar(255);
ie_classif_etaria_w	varchar(3);
qt_idade_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	classificacao_etaria
where	cd_estabelecimento	= cd_estabelecimento_p;

if (qt_existe_w > 0) then
	begin
	select	dt_nascimento,
		coalesce(dt_obito,clock_timestamp())
	into STRICT	dt_nascimento_w,
		dt_obito_w
	from	pessoa_fisica
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p;
	
	select	(obter_idade(dt_nascimento_w,dt_obito_w,'A'))::numeric
	into STRICT	qt_idade_w
	;

	select	coalesce(max(ie_classif_etaria),'A')
	into STRICT	ie_classif_etaria_w
	from	classificacao_etaria
	where	cd_estabelecimento	= cd_estabelecimento_p
	and	qt_idade_w between qt_idade_minima and qt_idade_maxima;

	if (ie_codigo_descr_p = 'C') then
		ds_retorno_w	:= ie_classif_etaria_w;
	else
		select	max(ds_expressao)
		into STRICT	ds_retorno_w
		from	valor_dominio_v
		where	cd_dominio	= 1698
		and	vl_dominio	= ie_classif_etaria_w;
	end if;
	end;
else
	begin
	if (ie_codigo_descr_p = 'C') then
		ds_retorno_w	:= 'A';
	else
		select	max(ds_expressao)
		into STRICT	ds_retorno_w
		from	valor_dominio_v
		where	cd_dominio	= 1698
		and	vl_dominio	= 'A';
	end if;
	end;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
