CREATE OR REPLACE FUNCTION obter_classif_fiscal_nota ( nr_sequencia_p bigint, ie_desc_codigo_p text) RETURNS varchar AS $body$
DECLARE


/*ie_desc_codigo_p
C = Codigo
D = Descrição
CD = Código - Descrição
*/
cd_classificacao_w		varchar(15);
ds_classificacao_w		varchar(150);
ds_retorno_w			varchar(150);


BEGIN

select	cd_classificacao,
	ds_classificacao
into STRICT	cd_classificacao_w,
	ds_classificacao_w
from	classif_fiscal_prest_serv
where	nr_sequencia = nr_sequencia_p;

if (ie_desc_codigo_p = 'C') then
	ds_retorno_w	:= cd_classificacao_w;
elsif (ie_desc_codigo_p = 'D') then
	ds_retorno_w	:= ds_classificacao_w;
elsif (ie_desc_codigo_p = 'CD') then
	ds_retorno_w	:= cd_classificacao_w || ' - ' || ds_classificacao_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
