CREATE OR REPLACE FUNCTION obter_classif_intpd (nr_seq_evento_sistema_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_classificacao_w			CLASSIFICACAO_INTEGRACAO.ds_classificacao%type;
nr_seq_classif_w			CLASSIFICACAO_INTEGRACAO.nr_sequencia%type;


BEGIN

select	max(c.ds_classificacao),
		max(c.nr_sequencia)
into STRICT	ds_classificacao_w,
		nr_seq_classif_w
from	classificacao_integracao c,
		intpd_eventos b,
		intpd_eventos_sistema a
where	a.nr_seq_evento	= b.nr_sequencia
and		b.nr_seq_classif = c.nr_sequencia
and		a.nr_sequencia = nr_seq_evento_sistema_p;

if (ie_opcao_p = 'D') then
	return	ds_classificacao_w;
else
	return	nr_seq_classif_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
