CREATE OR REPLACE FUNCTION obter_classif_medico_atend (nr_atendimento_p bigint, cd_medico_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_classif_medico_w		bigint	:= null;
ds_especialidade_w		varchar(255)	:= '';

C01 CURSOR FOR
SELECT	nr_seq_classif_medico
from	atendimento_troca_medico
where	cd_medico_atual = cd_medico_p
and 	nr_atendimento = nr_atendimento_p
order by nr_sequencia;


BEGIN

OPEN C01;
LOOP
FETCH C01 into
	nr_seq_classif_medico_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	nr_seq_classif_medico_w	:= nr_seq_classif_medico_w;
END LOOP;
CLOSE C01;

if (nr_seq_classif_medico_w IS NOT NULL AND nr_seq_classif_medico_w::text <> '') then

	select	ds_classificacao
	into STRICT	ds_especialidade_w
	from	medico_classif_atend
	where	nr_sequencia	= nr_seq_classif_medico_w;

end if;

RETURN ds_especialidade_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
