SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_classif_mort_inf (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
qt_dias_obito_w		bigint;
ds_classif_obito_w	varchar(240);


BEGIN 
 
select	trunc(a.dt_alta - b.dt_nascimento) 
into STRICT	qt_dias_obito_w 
from	pessoa_fisica b, 
	atendimento_paciente a 
where 	b.cd_pessoa_fisica	= a.cd_pessoa_fisica 
and	a.nr_atendimento	= nr_atendimento_p;
 
if (qt_dias_obito_w	< 8) then 
	ds_classif_obito_w	:= Wheb_mensagem_pck.get_texto(308862); --'Mortalidade neonatal precoce'; 
elsif (qt_dias_obito_w	between 8 and 28) then
	ds_classif_obito_w	:= Wheb_mensagem_pck.get_texto(308863); --'Mortalidade neonatal tardia'; 
elsif (qt_dias_obito_w	between 29 and 365) then
	ds_classif_obito_w	:= Wheb_mensagem_pck.get_texto(308864); --'Mortalidade infantil'; 
end if;
 
RETURN	ds_classif_obito_w;
 
END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_classif_mort_inf (nr_atendimento_p bigint) FROM PUBLIC;

