CREATE OR REPLACE FUNCTION obter_classif_motivo_auditoria (nr_seq_motivo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(80);
nr_seq_classif_w	bigint;


BEGIN

if (nr_seq_motivo_p IS NOT NULL AND nr_seq_motivo_p::text <> '') then
	select 	nr_seq_classificacao
	into STRICT	nr_seq_classif_w
	from 	auditoria_motivo
	where	nr_sequencia    = nr_seq_motivo_p;

	select	ds_classificacao
	into STRICT 	ds_retorno_w
	from	auditoria_motivo_classif
	where	nr_sequencia = nr_seq_classif_w;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
