CREATE OR REPLACE FUNCTION obter_classif_obito (dt_entrada_p timestamp, dt_alta_p timestamp) RETURNS varchar AS $body$
DECLARE


qt_obito_hs_w		bigint;
ds_retorno_w		varchar(240);


BEGIN

qt_obito_hs_w	:= trunc((dt_alta_p - (dt_entrada_p)) * 24);

if (qt_obito_hs_w between 0 and 47) then
	ds_retorno_w	:= Wheb_mensagem_pck.get_texto(308865); --'Óbito antes de 48 h';
else
	ds_retorno_w	:= Wheb_mensagem_pck.get_texto(308866); --'Óbito após 48 h';
end if;

return	ds_retorno_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
