SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_classif_percentil (ie_raca_negra_p text, ie_sexo_p text, qt_idade_p bigint, ie_opcao_p text ) RETURNS bigint AS $body$
DECLARE

cd_estabelecimento_w       smallint;				
ie_regra_w                 regra_classif_percentil.ie_referencia%type := 'N';
qt_perc_prega_cut_tricip_w real;
qt_perc_circ_braco_w       real;
qt_perc_circ_amb_w         double precision;	
qt_perc_circ_musc_braco_w  real;
qt_retorno_w               double precision := 0;
ie_referencia_fri_w        regra_classif_percentil.IE_REFERENCIA_FRI%type;
ie_versao_w                w_aval_who.ie_versao%type;
EXEC_W                     varchar(200);

BEGIN
	cd_estabelecimento_w := Obter_estab_usuario(wheb_usuario_pck.get_nm_usuario);

	select	coalesce(MAX(ie_referencia),'N'), coalesce(max(IE_REFERENCIA_FRI ), 'N')
	into STRICT	ie_regra_w, ie_referencia_fri_w
	from	regra_classif_percentil 
	where	cd_estabelecimento = cd_estabelecimento_w
	and 	qt_idade_p between coalesce(QT_IDADE_MIN,0) and coalesce(QT_IDADE_MAX,999);
	
	if (ie_opcao_p = 'AMB') and (ie_referencia_fri_w = 'AM') then
    begin
      select CASE WHEN ie_regra_w='P' THEN  1984 WHEN ie_regra_w='F' THEN  1981 WHEN ie_regra_w='F3' THEN  1990 WHEN ie_regra_w='F2' THEN  2008 WHEN ie_regra_w='S' THEN  2001 END
      into STRICT ie_versao_w
;

      begin
        EXEC_W := 'CALL OBTER_PERC_C_AMB_CLAS_PER_MD(:1,:2,:3,:4) INTO :RESULT';

        EXECUTE EXEC_W USING IN ie_regra_w,
                                       IN ie_versao_w,
                                       IN ie_sexo_p,
                                       IN qt_idade_p,
                                       OUT qt_perc_circ_amb_w;
      exception
        when others then
          qt_perc_circ_amb_w := null;
      end;
    end;
	end if;	

  begin
    EXEC_W := 'BEGIN CALC_PRE_CIRC_CLAS_PERCEN_MD(:1,:2,:3,:4,:5,:6,:7,:8); END;';

    EXECUTE EXEC_W USING IN ie_regra_w,
                                   IN ie_opcao_p,
                                   IN ie_referencia_fri_w,
                                   IN ie_sexo_p,
                                   IN qt_idade_p,
                                   OUT qt_perc_prega_cut_tricip_w,
                                   OUT qt_perc_circ_braco_w,
                                   OUT qt_perc_circ_musc_braco_w;
  exception
    when others then
      qt_perc_prega_cut_tricip_w := null;
      qt_perc_circ_braco_w       := null;
      qt_perc_circ_musc_braco_w  := null;
  end;

  begin
    EXEC_W := 'CALL OBTER_RET_CLASS_PERCENTIL_MD(:1,:2,:3,:4,:5) INTO :RESULT';

    EXECUTE EXEC_W USING IN ie_opcao_p,
                                   IN qt_perc_prega_cut_tricip_w,
                                   IN qt_perc_circ_braco_w,
                                   IN qt_perc_circ_amb_w,
                                   IN qt_perc_circ_musc_braco_w,
                                   OUT qt_retorno_w;
  exception
    when others then
      qt_retorno_w := null;
  end;

	return qt_retorno_w;
	
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
