CREATE OR REPLACE FUNCTION obter_classif_peso_nasc (nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE

 
ie_classif_peso_w	niss_invasividade.ie_classif_peso%type;
cd_pessoa_fisica_w	pessoa_fisica.cd_pessoa_fisica%type;


BEGIN 
 
select	obter_classif_peso_neo(coalesce(max(n.qt_peso_sala_parto),0)/1000) 
into STRICT	ie_classif_peso_w 
from	nascimento n 
where	n.nr_atendimento = nr_atendimento_p;
 
if (coalesce(ie_classif_peso_w::text, '') = '') then 
	select	obter_classif_peso_neo(obter_primeiro_sinal_vital(nr_atendimento_p,'Peso')) 
	into STRICT	ie_classif_peso_w 
	;
 
	if (coalesce(ie_classif_peso_w::text, '') = '') then 
 
		select	max(cd_pessoa_fisica) 
		into STRICT	cd_pessoa_fisica_w 
		from	atendimento_paciente 
		where	nr_atendimento = nr_atendimento_p;
 
		select	obter_classif_peso_neo(coalesce(p.qt_peso_nasc,0)/1000) 
		into STRICT	ie_classif_peso_w 
		from	pessoa_fisica p 
		where	p.cd_pessoa_fisica = cd_pessoa_fisica_w;
 
	end if;
end if;
 
return coalesce(ie_classif_peso_w,0);
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_classif_peso_nasc (nr_atendimento_p bigint) FROM PUBLIC;

