CREATE OR REPLACE FUNCTION obter_classif_peso_neo ( qt_peso_p bigint ) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(15);
qt_peso_gramas_w	double precision;


BEGIN

qt_peso_gramas_w := qt_peso_p * 1000;

IF (qt_peso_p > 0) THEN
	IF (qt_peso_gramas_w < 750) THEN
		ie_retorno_w	:= 10;
	ELSIF (qt_peso_gramas_w >= 750) AND (qt_peso_gramas_w < 1000) THEN
		ie_retorno_w	:= 20;
	ELSIF (qt_peso_gramas_w >= 1000) AND (qt_peso_gramas_w < 1500) THEN
		ie_retorno_w	:= 30;
	ELSIF (qt_peso_gramas_w >= 1500) AND (qt_peso_gramas_w < 2500) THEN
		ie_retorno_w	:= 40;
	ELSIF (qt_peso_gramas_w >= 2500) THEN
		ie_retorno_w	:= 50;
	END IF;
END IF;

RETURN	ie_retorno_w;

END;
