CREATE OR REPLACE FUNCTION obter_classif_pessoa_data (cd_cgc_p text, cd_pessoa_fisica_p text, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


nr_seq_classif_w	bigint;


BEGIN

nr_seq_classif_w	:= null;

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	select	max(nr_seq_classif)
	into STRICT	nr_seq_classif_w
	from	pessoa_classif u
	where	coalesce(dt_referencia_p, clock_timestamp()) between coalesce(DT_INICIO_VIGENCIA, clock_timestamp() - interval '1 days') and coalesce(DT_FINAL_VIGENCIA, clock_timestamp() + interval '1 days')
	and	u.cd_pessoa_fisica	= cd_pessoa_fisica_p;

elsif (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') then

	select	max(nr_seq_classif)
	into STRICT	nr_seq_classif_w
	from	pessoa_classif u
	where	coalesce(dt_referencia_p, clock_timestamp()) between coalesce(DT_INICIO_VIGENCIA, clock_timestamp() - interval '1 days') and coalesce(DT_FINAL_VIGENCIA, clock_timestamp() + interval '1 days')
	and	u.cd_cgc		= cd_cgc_p;

end if;

return	nr_seq_classif_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
