CREATE OR REPLACE FUNCTION obter_classif_pessoa_status (nr_seq_classif_p bigint) RETURNS varchar AS $body$
DECLARE

/** Retorna o status da classificação da pessoa
 *  Juliane Menin - 25/10/2007
*/
ie_retorno_w	varchar(80);


BEGIN
if (nr_seq_classif_p IS NOT NULL AND nr_seq_classif_p::text <> '') then
	select	ie_situacao
	into STRICT	ie_retorno_w
	from	classif_pessoa
	where	nr_sequencia = nr_seq_classif_p;
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
