CREATE OR REPLACE FUNCTION obter_classif_relatorio ( nr_sequencia_p bigint, opcao_p text) RETURNS varchar AS $body$
DECLARE

 
 
ds_titulo_w		varchar(200);
cd_classif_relat_w	varchar(4);
cd_relatorio_w		integer;
ds_retorno_w		varchar(200);


BEGIN 
	select	ds_titulo, 
		cd_classif_relat, 
		cd_relatorio 
	into STRICT	ds_titulo_w, 
		cd_classif_relat_w, 
		cd_relatorio_w 
	from	relatorio 
	where	nr_sequencia		= nr_sequencia_p;
 
if ( opcao_p = 'CL') then 
	ds_retorno_w := cd_classif_relat_w;
elsif (opcao_p = 'C') then 
	ds_retorno_w := cd_relatorio_w;
elsif (opcao_p = 'T') then 
	ds_retorno_w := cd_classif_relat_w || to_char(cd_relatorio_w, '00000');
end if;
 
return	ds_retorno_w;
 
end;
