CREATE OR REPLACE FUNCTION obter_classif_servico ( dt_inicial_p timestamp, dt_final_p timestamp, nr_atendimento_p bigint, cd_setor_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w varchar(255);

BEGIN
/*ie_opcao_p
    D = descrição
    C = código*/
if (ie_opcao_p = 'C') then
	  select  to_char(min(nr_seq_classif_servico))
	  into STRICT  ds_retorno_w
	  from   nut_atend_serv_dia
	  where  nr_atendimento = nr_atendimento_p
	  and  cd_setor_atendimento = cd_setor_atendimento_p
	  and dt_servico between dt_inicial_p and dt_final_p;
else
	  select max(a.ds_classificacao)
	  into STRICT ds_retorno_w
	  from nut_atend_serv_classif a
	  where a.ie_prioridade = (   	 SELECT  min(x.nr_seq_classif_servico)
					 from  nut_atend_serv_dia x
					 where  nr_atendimento = nr_atendimento_p
					 and cd_setor_atendimento = cd_setor_atendimento_p
					 and dt_servico between dt_inicial_p and dt_final_p);
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
