CREATE OR REPLACE FUNCTION obter_classif_tiss_agenda (nr_seq_agenda_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


ie_tipo_retorno_w	smallint;
ie_classif_agenda_w	varchar(5);


BEGIN
if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') then
	select	ie_classif_agenda
	into STRICT	ie_classif_agenda_w
	from	agenda_consulta
	where	nr_sequencia = nr_seq_agenda_p;

	if (ie_classif_agenda_w IS NOT NULL AND ie_classif_agenda_w::text <> '') then
		select	CASE WHEN ie_opcao_p='C' THEN  coalesce(max(ie_tipo_consulta),1) WHEN ie_opcao_p='S' THEN  coalesce(max(ie_tipo_saida_consulta),1) END
		into STRICT	ie_tipo_retorno_w
		from	agenda_classif
		where	cd_classificacao = ie_classif_agenda_w;
	end if;
end if;

return ie_tipo_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
