CREATE OR REPLACE FUNCTION obter_classif_tratamento (nr_seq_tratamento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(80);
nr_seq_classif_w	bigint;


BEGIN

select	max(nr_seq_classif)
into STRICT	nr_seq_classif_w
from	rp_tipo_tratamento
where	nr_sequencia = nr_seq_tratamento_p;


if (nr_seq_classif_w IS NOT NULL AND nr_seq_classif_w::text <> '') then
	select	ds_classificacao
	into STRICT	ds_retorno_w
	from	rp_classif_tratamento
	where	nr_sequencia = nr_seq_classif_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
