CREATE OR REPLACE FUNCTION obter_clave_idade_nom ( dt_nascimento_p timestamp, dt_referencia_p timestamp, ie_tipo_informacao_p text) RETURNS bigint AS $body$
DECLARE


ds_retorno_w bigint;


BEGIN

if ((ie_tipo_informacao_p = 'EDAD') or (ie_tipo_informacao_p = 'CLAVE')) then
	if (pkg_date_utils.get_DiffDate(dt_nascimento_p, clock_timestamp(), 'HOUR') <= 23) then
		if (ie_tipo_informacao_p = 'EDAD') then
			ds_retorno_w := pkg_date_utils.get_DiffDate(dt_nascimento_p, clock_timestamp(), 'HOUR');
		else
			ds_retorno_w := 0;
		end if;
	else
		if (obter_idade(dt_nascimento_p, dt_referencia_p, 'M') = 0) then
			if (ie_tipo_informacao_p = 'EDAD') then
				ds_retorno_w := obter_idade(dt_nascimento_p, dt_referencia_p, 'DIAS');
			else
				ds_retorno_w := 1;
			end if;
		elsif (obter_idade(dt_nascimento_p, dt_referencia_p, 'M') < 12) then
			if (ie_tipo_informacao_p = 'EDAD') then
				ds_retorno_w := obter_idade(dt_nascimento_p, dt_referencia_p, 'M');
			else
				ds_retorno_w := 2;
			end if;
		elsif (obter_idade(dt_nascimento_p, dt_referencia_p, 'M') >= 12) then
			if (ie_tipo_informacao_p = 'EDAD') then
				ds_retorno_w := obter_idade(dt_nascimento_p, dt_referencia_p, 'A');
			else
				ds_retorno_w := 3;
			end if;
		end if;
	end if;
end if;

return ds_retorno_w;

end;
$body$
