SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_clave_unidad_serv_prod ( nr_sequencia_p text, ie_tipo_clave_p text ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);

-- UN clave unidad

-- PS clave produto / servicio

-- IMP impuesto
BEGIN

if (ie_tipo_clave_p = 'UN') then
	
	select 	max(coalesce(a.cd_chave, 'E48')) || ' - ' || max(a.ds_chave) || ' ' || max(a.ds_chave_compl)
	into STRICT	ds_retorno_w
	from 	nf_imp_chave a,
		nf_imp_nota_item b, 
		nf_imp_nota c 
	where	a.nr_sequencia = b.nr_seq_chave_unidade
	and	b.nr_seq_nf_imp_nota = c.nr_sequencia
	and	c.nr_seq_nf_interno = nr_sequencia_p;

elsif (ie_tipo_clave_p = 'PS') then
	
	select 	max(a.cd_chave) || ' - ' || max(a.ds_chave) || ' ' || max(a.ds_chave_compl)
	into STRICT	ds_retorno_w
	from 	nf_imp_chave a,
		nf_imp_nota_item b, 
		nf_imp_nota c 
	where	a.nr_sequencia = b.nr_seq_chave_prod_serv
	and	b.nr_seq_nf_imp_nota = c.nr_sequencia
	and	c.nr_seq_nf_interno = nr_sequencia_p;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
