CREATE OR REPLACE FUNCTION obter_cliente_defeito (nr_seq_ordem_p text) RETURNS varchar AS $body$
DECLARE


ie_doc_cliente_w	varchar(1):= 'N';
ie_origem_erro_w	varchar(4);
ie_possui_doc_w		varchar(1);


BEGIN
if (coalesce(nr_seq_ordem_p,0) > 0) then
	begin
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_possui_doc_w
	from	man_doc_erro
	where	nr_seq_ordem = nr_seq_ordem_p
	and 	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');

	if (coalesce(ie_possui_doc_w,'N') = 'S')	then
		select  max(ie_origem_erro)
		into STRICT 	ie_origem_erro_w
		from 	man_doc_erro
		where 	nr_seq_ordem = nr_seq_ordem_p;
	end if;


	if (ie_origem_erro_w  = 'C') then
		ie_doc_cliente_w:= 'S';
	else
		ie_doc_cliente_w:= 'N';
	end if;
	end;
end if;

return	ie_doc_cliente_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
