CREATE OR REPLACE FUNCTION obter_clinica_atend ( nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ie_clinica_w	atendimento_paciente.ie_clinica%type;

BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	select	max(ie_clinica)
	into STRICT	ie_clinica_w
	from 	atendimento_paciente
	where 	nr_atendimento	= nr_atendimento_p;

	if (ie_opcao_p	= 'C') then
		return ie_clinica_w;
	else
		return obter_valor_dominio(17, ie_clinica_w);
	end if;
end if;

return 	'';

end;
