SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_clinica_conta ( nr_interno_conta_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
ds_clinica_w		varchar(100);
ie_clinica_w		varchar(15);
ds_retorno_w		varchar(100);
nr_atendimento_w	bigint;


BEGIN 
 
select	nr_atendimento 
into STRICT	nr_atendimento_w 
from 	conta_paciente 
where	nr_interno_conta = nr_interno_conta_p;
 
select	obter_valor_dominio(17, ie_clinica), 
	ie_clinica 
into STRICT	ds_clinica_w, 
	ie_clinica_w 
from 	atendimento_paciente 
where 	nr_atendimento	= nr_atendimento_w;
 
if (ie_opcao_p	= 'C') then 
	ds_retorno_w	:= ie_clinica_w;
else 
	ds_retorno_w	:= ds_clinica_w;
end if;
 
return 	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
