CREATE OR REPLACE FUNCTION obter_clinica_pac_agenda (dt_agenda_p timestamp, cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ie_clinica_w	integer;	
ds_retorno_w	varchar(255);		
			
C01 CURSOR FOR
	SELECT	ie_clinica
	from	pessoa_classif
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	((dt_agenda_p >= dt_inicio_vigencia) or (coalesce(dt_inicio_vigencia::text, '') = ''))
	and	((dt_agenda_p <= dt_final_vigencia) or (coalesce(dt_final_vigencia::text, '') = ''))
	and	(ie_clinica IS NOT NULL AND ie_clinica::text <> '')
	order by 1;			
			

BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	open C01;
	loop
	fetch C01 into	
		ie_clinica_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		ie_clinica_w := ie_clinica_w;
		end;
	end loop;
	close C01;
	
	if (ie_opcao_p = 'C') then
		ds_retorno_w	:= ie_clinica_w;
	else		
		select	max(ds_expressao)
		into STRICT	ds_retorno_w
		from	valor_dominio_v
		where	cd_dominio	= 17
		and	vl_dominio	= ie_clinica_w;		
	end if;
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
