CREATE OR REPLACE FUNCTION obter_cl_equipe_board ( cd_agenda_p bigint) RETURNS varchar AS $body$
DECLARE

			
text_participants		varchar(255);


BEGIN

if (cd_agenda_p IS NOT NULL AND cd_agenda_p::text <> '') then
	begin
	
		select	rtrim(xmlagg(XMLELEMENT(name e, substr(obter_nome_pf(cd_pessoa_fisica),1,255) || ', ') order by obter_nome_pf(cd_pessoa_fisica)).extract('//text()'), ', ')
		into STRICT	text_participants
		from ( SELECT distinct cd_pessoa_fisica from agenda_turno a, cl_equipe_board b
				where	a.nr_sequencia = b.nr_seq_turno
				and     a.cd_agenda = cd_agenda_p) alias8;	
		
	end;
end if;
	
return	text_participants;

end;
$body$
