CREATE OR REPLACE FUNCTION obter_cm_diario_estoque ( cd_estabelecimento_p bigint, dt_mesano_referencia_p timestamp, cd_material_p bigint, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE



vl_custo_medio_w		double precision;

c01 CURSOR FOR
SELECT	vl_custo_medio
from	saldo_estoque_cm_diario
where	cd_estabelecimento	= cd_estabelecimento_p
and	dt_mesano_referencia	= dt_mesano_referencia_p
and	cd_material		= cd_material_p
and	dt_referencia		<= dt_referencia_p
order by dt_referencia;


BEGIN

OPEN c01;
LOOP
FETCH c01 into vl_custo_medio_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	vl_custo_medio_w	:= vl_custo_medio_w;
END LOOP;
CLOSE c01;

return	coalesce(vl_custo_medio_w,0);

end;
