CREATE OR REPLACE FUNCTION obter_cm_mat_convertido ( cd_estabelecimento_p bigint, cd_material_p bigint, cd_unidade_medida_p text, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


vl_custo_medio_w			double precision;
cd_material_w			integer;
qt_conversao_w			double precision;
cd_unidade_medida_compra_w	varchar(30);
cd_unidade_medida_estoque_w	varchar(30);


BEGIN

select	a.cd_unidade_medida_compra,
	a.cd_unidade_medida_estoque
into STRICT	cd_unidade_medida_compra_w,
	cd_unidade_medida_estoque_w
from	material a
where	a.cd_material = cd_material_p;

select	obter_custo_medio_material(cd_estabelecimento_p, trunc(dt_referencia_p,'mm'), cd_material_p)
into STRICT	vl_custo_medio_w
;

if (cd_unidade_medida_p <> cd_unidade_medida_estoque_w) then
	begin

	select	coalesce(max(qt_conv_compra_estoque),1)
	into STRICT	qt_conversao_w
	from	material
	where	cd_material = cd_material_p;

	vl_custo_medio_w	:= (vl_custo_medio_w * qt_conversao_w);

	end;
end if;

return	vl_custo_medio_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
