CREATE OR REPLACE FUNCTION obter_cnae_emitente ( cd_estabelecimento_p bigint, cd_cgc_emitente_p text, nr_seq_item_serv_p bigint, cd_procedimento_p bigint ) RETURNS bigint AS $body$
DECLARE


cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;
cd_empresa_w		empresa.cd_empresa%type;
cd_procedimento_w	procedimento.cd_procedimento%type;
nr_seq_item_serv_w	procedimento.nr_seq_item_serv%type;
nr_seq_cnae_w		cnae_empresa_estab.nr_seq_cnae%type;
x boolean;

c01 CURSOR FOR
SELECT	max(nr_seq_cnae)
from	cnae_empresa_estab
where	coalesce(cd_estabelecimento, cd_estabelecimento_w) = cd_estabelecimento_w
and	coalesce(cd_empresa, cd_empresa_w) = cd_empresa_w
and	coalesce(nr_seq_serv_item, nr_seq_item_serv_w) = nr_seq_item_serv_w
and	cd_procedimento  = cd_procedimento_w
and	(nr_seq_cnae IS NOT NULL AND nr_seq_cnae::text <> '')
order by cd_estabelecimento desc,
	cd_empresa desc;


BEGIN
cd_procedimento_w  := coalesce(cd_procedimento_p,0);
nr_seq_item_serv_w := coalesce(nr_seq_item_serv_p,0);

if (coalesce(cd_estabelecimento_p,0) = 0) then
	select	coalesce(max(cd_estabelecimento),0)
	into STRICT	cd_estabelecimento_w
	from	estabelecimento
	where	cd_cgc = cd_cgc_emitente_p;
else
	cd_estabelecimento_w := cd_estabelecimento_p;
end if;

select	coalesce(max(cd_empresa),0)
into STRICT	cd_empresa_w
from	estabelecimento
where	cd_estabelecimento = cd_estabelecimento_w;

open c01;
loop
fetch c01 into	
	nr_seq_cnae_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
end loop;
close c01;

if	((coalesce(nr_seq_cnae_w::text, '') = '') and (cd_cgc_emitente_p <> '0')) then
	nr_seq_cnae_w := obter_dados_pf_pj(null, cd_cgc_emitente_p , 'CNAE');
end if;

return	coalesce(nr_seq_cnae_w, 0);

end;
