CREATE OR REPLACE FUNCTION obter_cnae_nfse (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 	varchar(50);	


BEGIN

select	max(pls_obter_cd_cnae(CASE WHEN obter_cnae_emitente(a.cd_estabelecimento, a.cd_cgc_emitente, obter_item_servico_proced(b.cd_procedimento, b.ie_origem_proced), b.cd_procedimento)=0 THEN			c.nr_seq_cnae  ELSE obter_cnae_emitente(a.cd_estabelecimento, a.cd_cgc_emitente, obter_item_servico_proced(b.cd_procedimento, b.ie_origem_proced), b.cd_procedimento) END ))
into STRICT	ds_retorno_w
from 	operacao_nota c,
	nota_fiscal_item b,
	nota_fiscal a
where	a.nr_sequencia = nr_sequencia_p
and	a.nr_sequencia = b.nr_sequencia
and 	a.cd_operacao_nf = c.cd_operacao_nf
and	CASE WHEN obter_cnae_emitente(a.cd_estabelecimento, a.cd_cgc_emitente, obter_item_servico_proced(b.cd_procedimento, b.ie_origem_proced), b.cd_procedimento)=0 THEN  			c.nr_seq_cnae  ELSE obter_cnae_emitente(a.cd_estabelecimento, a.cd_cgc_emitente, obter_item_servico_proced(b.cd_procedimento, b.ie_origem_proced), b.cd_procedimento) END  <> 0;
				
return	ds_retorno_w;

end;
$body$
