CREATE OR REPLACE FUNCTION obter_cobertura_conv_age_pac (nr_sequencia_p bigint, nr_seq_agenda_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE



ds_retorno_w	varchar(255);


BEGIN

if (ie_opcao_p = 'P') then

	if (coalesce(nr_sequencia_p,0) > 0) then

		select	max(obter_valor_dominio(7296,IE_COBERTURA_CONV))
		into STRICT	ds_retorno_w
		from   	agenda_paciente a
		where 	nr_sequencia = nr_sequencia_p;

	end if;

elsif (ie_opcao_p = 'A') then

	if (coalesce(nr_sequencia_p,0) > 0) and (coalesce(nr_seq_agenda_p,0) > 0)	then

		select	max(obter_valor_dominio(7296,IE_COBERTURA_CONV))
		into STRICT	ds_retorno_w
		from   	agenda_paciente_proc a
		where 	nr_sequencia = nr_sequencia_p
		and	nr_seq_agenda = nr_seq_agenda_p;

	end if;

end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
