CREATE OR REPLACE FUNCTION obter_cobrador_data (nr_seq_cobranca_p bigint, dt_referencia_p timestamp, ie_sem_alteracao_p text) RETURNS bigint AS $body$
DECLARE


dt_alteracao_w		timestamp;
nr_seq_cobrador_w	bigint;
nr_seq_cobr_atual_w	bigint;


BEGIN

select	max(dt_alteracao)
into STRICT	dt_alteracao_w
from	cobranca_alt_cobrador
where	nr_seq_cobranca		= nr_seq_cobranca_p
and	trunc(dt_alteracao, 'dd') <= trunc(dt_referencia_p, 'dd');

select	max(nr_seq_cobr_novo)
into STRICT	nr_seq_cobrador_w
from	cobranca_alt_cobrador
where	nr_seq_cobranca		= nr_seq_cobranca_p
and	dt_alteracao		= dt_alteracao_w;

if (coalesce(nr_seq_cobrador_w::text, '') = '') then
	/* Se nÃ£o achou, pega o cobrador anterior do primeiro registro */

	select	min(dt_alteracao)
	into STRICT	dt_alteracao_w
	from	cobranca_alt_cobrador
	where	nr_seq_cobranca	= nr_seq_cobranca_p;

	if (dt_alteracao_w IS NOT NULL AND dt_alteracao_w::text <> '') then

		select	max(nr_seq_cobr_ant)
		into STRICT	nr_seq_cobrador_w
		from	cobranca b,
			cobranca_alt_cobrador a
		where	a.nr_seq_cobranca	= b.nr_sequencia
		and	a.nr_seq_cobranca	= nr_seq_cobranca_p
		and	a.dt_alteracao		= dt_alteracao_w
		and	b.dt_inclusao		<= trunc(dt_referencia_p,'dd');
	end if;

	/* Se mesmo assim nao encontrou e desejar considerar as cobrancas sem registro de alteracao
		de cobrador */
	if (coalesce(nr_seq_cobrador_w::text, '') = '') and (coalesce(ie_sem_alteracao_p,'S') = 'S') then

		select	max(nr_seq_cobrador)
		into STRICT	nr_seq_cobr_atual_w
		from	cobranca a
		where	nr_sequencia	= nr_seq_cobranca_p
		and	not exists (SELECT	1
					from	cobranca_alt_cobrador x
					where	x.nr_seq_cobranca	= a.nr_sequencia)
		and	trunc(a.dt_inclusao,'dd')	<= trunc(dt_referencia_p,'dd');


		nr_seq_cobrador_w	:= nr_seq_cobr_atual_w;
	end if;
end if;

return	nr_seq_cobrador_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
