CREATE OR REPLACE FUNCTION obter_cobranca_tit_conpaci ( nr_interno_conta_p bigint) RETURNS varchar AS $body$
DECLARE


ds_cobranca_w		varchar(4000);
nr_seq_cobranca_w	bigint;
nr_titulo_w		bigint;

C01 CURSOR FOR
SELECT	a.nr_titulo
from	titulo_receber a
where	a.nr_interno_conta = nr_interno_conta_p;


BEGIN

open C01;
loop
fetch C01 into
	nr_titulo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	nr_seq_cobranca_w := substr(obter_cobranca_titulo(nr_titulo_w,'S'),1,10);

	if (coalesce(nr_seq_cobranca_w,0) <> 0) then
		ds_cobranca_w := substr(ds_cobranca_w || to_char(nr_seq_cobranca_w) || ',',1,4000);
	end if;

	end;
end loop;
close C01;

if (length(ds_cobranca_w) > 2) then
	ds_cobranca_w	:= substr(ds_cobranca_w,1,length(ds_cobranca_w) - 1);
end if;

return	ds_cobranca_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
