CREATE OR REPLACE FUNCTION obter_codificacao_cid (cd_doenca_p diagnostico_doenca.cd_doenca%type) RETURNS varchar AS $body$
DECLARE


ie_codificacao_w cid_doenca.ie_codificacao%type;


BEGIN
    if (cd_doenca_p IS NOT NULL AND cd_doenca_p::text <> '') then
        begin
            select  ie_codificacao
            into STRICT    ie_codificacao_w
            from    cid_doenca
            where   cd_doenca_cid = upper(cd_doenca_p);
        exception when others then
            ie_codificacao_w := null;
        end;
    end if;

    return ie_codificacao_w;
end;
$body$
