CREATE OR REPLACE FUNCTION obter_codigo_barras_ordem_frac ( nr_seq_ordem_p bigint, nr_seq_digito_p bigint default null) RETURNS varchar AS $body$
DECLARE


cd_barras_w		varchar(14)	:= null;
nr_seq_digito_w	adep_processo_frac.nr_seq_digito%type;


BEGIN
if (nr_seq_ordem_p IS NOT NULL AND nr_seq_ordem_p::text <> '') then

	if (coalesce(nr_seq_digito_p::text, '') = '') then
		select	max(nr_seq_digito)
		into STRICT	nr_seq_digito_w
		from	adep_processo_frac
		where	nr_sequencia = nr_seq_ordem_p;
	else
		nr_seq_digito_w	:= nr_seq_digito_p;
	end if;

	if (nr_seq_digito_w IS NOT NULL AND nr_seq_digito_w::text <> '') then
		cd_barras_w	:= '9' || lpad(nr_seq_ordem_p,10,0) || nr_seq_digito_w;
	end if;

end if;

return cd_barras_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
