CREATE OR REPLACE FUNCTION obter_codigo_barras_processo ( nr_seq_ordem_p bigint, cd_material_p bigint, ie_codigo_barras_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_digito_w	smallint;
cd_barras_w	varchar(14);


BEGIN
if (ie_codigo_barras_p = 'F') and (nr_seq_ordem_p IS NOT NULL AND nr_seq_ordem_p::text <> '') then
	select	max(nr_seq_digito)
	into STRICT	nr_seq_digito_w
	from	adep_processo_frac
	where	nr_sequencia = nr_seq_ordem_p;

	if (nr_seq_digito_w IS NOT NULL AND nr_seq_digito_w::text <> '') then
		cd_barras_w	:= '9' || lpad(nr_seq_ordem_p,10,0) || nr_seq_digito_w;
	else
		cd_barras_w	:= cd_material_p;
	end if;
else
	cd_barras_w	:= cd_material_p;
end if;

return cd_barras_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
